import { useState, React } from "react";
import logo from './logo.svg';
import useAsync from "./components/useAsync";
import { Input, Message, Button } from "semantic-ui-react";
import { unlockAccount } from "./api/web3";
import { useWeb3Context } from "./contexts/Web3";
import 'semantic-ui-css/semantic.min.css';
import Axios from 'axios';
import './App.css';

function Home() {

  const [filmName, setFilmName] = useState('');
  const [filmDescription, setFilmDescription] = useState('');
  const [filmCreator, setFilmCreator] = useState('');

  const {
    state: { account },
    updateAccount
  } = useWeb3Context();

  const { pending, error, call } = useAsync(unlockAccount);

  async function onClickConnect() {
    const { error, data } = await call(null);

    if (error) {
      console.error(error);
    }
    if (data) {
      updateAccount(data)
    }
  }

  const addData = () => {
    Axios.post("http://localhost:3001/insert", {
      filmName: filmName,
      description: filmDescription,
      creator: filmCreator,
    });
  }

  return (
    <div className="App">
      <div className="App-header">
        <h1>Testing</h1>
        <div>Account: {account}</div>
        <h1>Film Name</h1>
        <Input 
          type="text" 
          onChange={(e) => {
            setFilmName(e.target.value);
          }}
        />
        <h1>Film Creator</h1>
        <Input 
          type="text" 
          onChange={(e) => {
            setFilmCreator(e.target.value);
          }}
        />
        <h1>Film Description</h1>
        <Input 
          type="text" 
          onChange={(e) => {
            setFilmDescription(e.target.value);
          }}
        />
        <Button
          color="green"
          onClick={() => addData()}
        >Publish Video</Button>
        <Message warning>Metamask is not connected</Message>
        <Button 
          color="blue"
          onClick={() => onClickConnect()}
          disabled={pending}
          loading={pending}
        >Connect to Metamask</Button>
      </div>
    </div>
  );
}

export default App;
